export SHAPE_ENCODING='UTF-8'
ogr2ogr -f "ESRI Shapefile" -lco ENCODING=UTF-8 -s_srs "EPSG:4326" -t_srs "EPSG:4326" -overwrite bano-$1-`date "+%Y%m%d"`.shp PG:"dbname=cadastre" -sql "select * from (select concat(u.fantoir,'-',u.num) as id, upper(replace(case when u.num=o.num then o.numero else c.numero end,' ','')) as numero,coalesce(case when u.num=o.num then o.voie_cadastre else c.voie_cadastre end, case when u.num=o.num then o.voie_osm else (case when c.voie_osm !='' then c.voie_osm else c.voie_cadastre end) end) as voie, cn.nom,case when u.num=o.num then o.source else (case when c.voie_osm != '' then 'C+O' else 'CAD' end) end as source, case when u.num=o.num then st_y(o.geometrie) else st_y(c.geometrie) end as lat,case when u.num=o.num then st_x(o.geometrie) else st_x(c.geometrie) end as lon,case when u.num=o.num then o.geometrie else c.geometrie end as geo from (select fantoir,num from (select *,replace(replace(replace(replace(replace(replace(replace(regexp_replace(upper(numero),'^0*',''),'BIS','B'),'TER','T'),'QUATER','Q'),'QUAT','Q'),' ',''),'à','-'),';',',') as num from cumul_adresses where fantoir like '$1%' and fantoir is not null) as a group by fantoir,num) as u left join (select *,replace(replace(replace(replace(replace(replace(replace(regexp_replace(upper(numero),'^0*',''),'BIS','B'),'TER','T'),'QUATER','Q'),'QUAT','Q'),' ',''),'à','-'),';',',') as num from cumul_adresses where source='OSM') as o on (o.num=u.num and o.fantoir=u.fantoir) left join (select *,replace(replace(replace(replace(replace(replace(replace(regexp_replace(upper(numero),'^0*',''),'BIS','B'),'TER','T'),'QUATER','Q'),'QUAT','Q'),' ',''),'à','-'),';',',') as num from cumul_adresses where source='CADASTRE' and st_x(geometrie)!=0 and st_y(geometrie)!=0) as c on (c.num=u.num and c.fantoir=u.fantoir) join communes cn on (cn.insee=left(u.fantoir,5) ) ) as bano where lat is not null and lon is not null"
cp /data/work/cadastre.openstreetmap.fr/data/bano/*.txt ./
zip -q -9 bano-$1-`date "+%Y%m%d"`-shp.zip bano-$1-`date "+%Y%m%d"`.* *.txt
chmod a+r *.zip
mv bano-$1-`date "+%Y%m%d"`-shp.zip /data/work/cadastre.openstreetmap.fr/data/bano/
rm bano-*

